import osfrom pynput.keyboard import Key, Controllerimport timePATH = '/Users/kylece/Projects/ivc-dk_engine_jsbundle/ivc-dk-engine-speechrecognition-event'CP_SCH_CMD = 'pbcopy < %s/schema/data.json' % PATHCP_MOCK_CMD = 'pbcopy < %s/mock/data.json' % PATHCP_DES_CMD = 'pbcopy < %s/description/data.json' % PATHdef paste(key):    key.press(Key.cmd)    key.press('v')    key.release('v')    key.release(Key.cmd)def paste_tab4next(key, cmd):    os.popen(cmd)    # paste    key.press(Key.cmd)    key.press('v')    key.release('v')    key.release(Key.cmd)    time.sleep(0.5)    keyboard.type('\t')if __name__ == '__main__':    print('open web, auto fill in will start in 2s')    time.sleep(1)    keyboard = Controller()    paste_tab4next(keyboard, CP_SCH_CMD)    paste_tab4next(keyboard, CP_MOCK_CMD)    paste_tab4next(keyboard, CP_DES_CMD)