import fileinputimport sysfrom PIL import Imagefrom PIL import ImageFontfrom PIL import ImageDrawimport os# (left, upper, right, lower)def crop_image(in_file, root, not_phone=False):    full_name = os.path.join(root, in_file)    img = Image.open(full_name)    width, height = img.size    print('%d + %d + %s' % (width, height, full_name))    cropped = img.crop((0, 468, width, height - 522)) \        if not_phone else img.rotate(270).crop(        (505, 0, width - 521, height)).resize((2080, 2092))    out_full_name = os.path.join(root, 'crop_' + in_file)    cropped.save(out_full_name)def walk(path):    for root, dirs, files in os.walk(path, topdown=False):        for name in files:            if name.startswith('.'):                continue            crop_image(name, root, name.startswith('IMG'))if __name__ == '__main__':    #     argv = sys.argv    #     url = argv[1]    #     print(url)    #     replaceAll(FILE_PATH, url)    #     im = Image.open("bride.jpg")    #     im.rotate(45).show()    # walk('.')    walk('/Users/kylece/Documents/baia/pics/')    # crop_image('in.jpg', '.')    # crop_image('in1.jpg', '.', True)    # crop_image('in2.jpg', '.')