import FunctionCommon as Funimport scheduleimport timeQUIT_CMD = "osascript -e 'tell application \"%s\" to quit'"OPEN_CMD = "open -a \"%s\""REMOVE_CMD = "rm -rf /Applications/%s.app"CP_CMD = "cp -a /Users/elex/Documents/software/%s.app  /Applications"def restart_app(app):    Fun.p_open(QUIT_CMD % app)    Fun.p_open(REMOVE_CMD % app)    Fun.p_open(CP_CMD % app)    time.sleep(2)    Fun.p_open(OPEN_CMD % app)def schedule_restart(app, period, debug=False):    if debug:        schedule.every(period).seconds.do(restart_app, app)    else:        schedule.every(period).minutes.do(restart_app, app)if __name__ == '__main__':    smooth = 'SmoothScroll'    restart_app(smooth)    # schedule_restart('SmoothScroll', 5, True)    schedule_restart(smooth, 50)    while True:        schedule.run_pending()        time.sleep(1)