import fileinputimport sysFILE_PATH = '/Users/kylece/Downloads/mock.json'URL_JSON_KEY = '"interactiveUrl"'URL_JSON_FORMAT = '"interactiveUrl":"%s",'def replaceAll(file, url):    for line in fileinput.input(file, inplace=1):        if URL_JSON_KEY in line:            # old = line            line = URL_JSON_FORMAT % url + '\n'        sys.stdout.write(line)if __name__ == '__main__':    argv = sys.argv    url = argv[1]    print(url)    replaceAll(FILE_PATH, url)